insert into story(story_id,title,story_body,published_date, created_by) values (4,'Comet robot Philae phones home again', 'Europeâ€™s comet lander has again been in touch with Earth. The Philae probe made three short contacts of about 10 seconds each at roughly 2130 GMT on Sunday. Controllers at the European Space Agency said the contacts were briefer than they had hoped, but proved the little robot was in encouragingly good health after its seven-month slumber. Philae landed on Comet 67P in November and worked for 60 hours before its battery ran flat.','2017-09-17','admin');
